    Function.prototype.aopBefore = function(fn){
      console.log(this)
      // 第一步：保存原函数的引用
      const _this = this
      // 第四步：返回包括原函数和新函数的“代理”函数
      return function() {
        // 第二步：执行新函数，修正this
