/**
* 函数节流
*/
function throttle (fn, interval = 500) {
  let timer = null;
  let isFirst = true;

  return function () {
    let args = arguments;
    let _this = this

    if (isFirst) {
      // 如果是第一次运行，则直接执行函数
      fn.apply(_this, args)
      return isFirst = false;
    }

    if (timer) {
      // 如果定时器存在，则不执行任何动作
      return false;
    }

    timer = setTimeout(function() {
      clearTimeout(timer);
      timer = null;
      fn.apply(_this, args);
    }, interval)
  }
}

// 调用节流
